mapper.select:
/* 자격증 전체 조회 */ SELECT pc.user_certificate_code AS certificateCode, pc.user_id AS userId, pc.user_certificate_name AS certificateName, pc.profile_reg_date AS profileRegDate FROM profile_certificate AS pc
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY pc.profile_reg_date DESC
LIMIT #{startIndex}, #{rowPerPage};
