mapper.update:
    	/* 전문 과제 수정 */
		    UPDATE pro_project
			
				
					skill = #{skill},
				
				
					details = #{details},
				
				
					max_total = #{maxTotal},
				
				
					project_price = #{proProjectPrice},
				
				
					due_date = #{dueDate}
				
			
				WHERE
					pro_project_code = #{proProjectCode};

mapper.select:
	/* 참야분야 리스트 조회 */
	SELECT
		*
	FROM
		join_cate;

	/* 작업분야 리스트 조회 */
	SELECT
		*
	FROM
		work_cate;

	/* 주제분야 리스트 조회 */
	SELECT
		*
	FROM
		subject_cate;
