/* 공고코드 자동 증가 코드 */
SELECT CASE WHEN COUNT(*) = 0 THEN 'announcement_code001' WHEN CAST(SUBSTRING_INDEX(announcement_code,'code',-1) AS UNSIGNED) < 999 THEN CONCAT('announcement_code',LPAD(MAX(CAST(SUBSTRING_INDEX(announcement_code,'code',-1) AS UNSIGNED))+1,3,'0')) ELSE CONCAT('announcement_code',MAX(CAST(SUBSTRING_INDEX(announcement_code,'code',-1) AS UNSIGNED))+1) END AS newAnnouncementCode FROM announcement
/* 채용공고 등록 */ INSERT INTO announcement (announcement_code, cp_id, cp_name, cp_cate_code, join_cate_code, join_cate_name, area_code, area_cate, job_opening, required_skills, career, recruitment, wage, start_date, due_date, hiring_status_code, hiring_status) VALUES (#{announcementCode}, 'cp004', '엘지SW', 'cp_cate_code004', 'join_cate_code004', #{joinCateName}, 'area_code004', #{areaCate}, #{jobOpening}, #{requiredSkills}, #{career}, #{recruitment}, #{wage}, CURDATE(), CURDATE(), 'hiring_status_code01', #{hiringStatus});
