mapper.insert:
/*자기소개 자동 증가 코드*/
SELECT CASE WHEN COUNT(*) = 0 THEN 'user_skill_code001' WHEN CAST(SUBSTRING_INDEX(user_skill_code, 'user_skill_code',-1) AS UNSIGNED) < 999 THEN CONCAT('user_skill_code', LPAD(MAX(CAST(SUBSTRING_INDEX(user_skill_code, 'user_skill_code',-1) AS UNSIGNED))+1,3,'0')) ELSE CONCAT('user_skill_code', MAX(CAST(SUBSTRING_INDEX(user_skill_code, 'user_skill_code',-1) AS UNSIGNED))+1) END AS newSkillCode FROM profile_skill
/*회원가입*/ INSERT INTO profile_skill( user_skill_code, user_id, user_skill, user_skill_rank, user_experience, user_reg_date, profile_hidden, profile_application_hidden ) VALUES( #{userSkillCode}, 'id010', #{userSkill}, #{userSkillRank}, #{userExperience}, CURDATE(), #{profileHidden}, #{profileApplicationHidden});

mapper.update:
/*보유기술 수정*/ UPDATE profile_skill
user_skill = #{userSkill},
profile_hidden = #{profileHidden},
profile_application_hidden = #{profileApplicationHidden},
WHERE user_skill_code = #{userSkillCode}
