/* 채용 공고 자동 증가 코드 */
SELECT CASE WHEN COUNT(*) = 0 THEN 'company_employment_code001' WHEN CAST(SUBSTRING_INDEX(company_employment_code,'code',-1) AS UNSIGNED) < 999 THEN CONCAT('company_employment_code',LPAD(MAX(CAST(SUBSTRING_INDEX(company_employment_code,'code',-1) AS UNSIGNED))+1,3,'0')) ELSE CONCAT('company_employment_code',MAX(CAST(SUBSTRING_INDEX(company_employment_code,'code',-1) AS UNSIGNED))+1) END AS newRecommendEmploymentCode FROM company_employment
INSERT INTO company_employment (company_employment_code, company_support_code, user_id, announcement_code, join_cate_code, join_cate_name, area_code, area_cate, job_opening, required_skills, career, wage, start_date, due_date, job_normal_run_code, job_normal_run) VALUE (#{companyEmploymentCode}, 'company_support_code001', 'id001', 'announcement_code001', 'join_cate_code001', #{joinCateName}, 'area_code001', #{areaCate}, #{jobOpening}, #{requiredSkills}, #{career}, #{wage}, CURDATE(), CURDATE(), 'job_normal_run_code001', #{jobNormalRun})
